// Update a Book by the id in the request
exports.recoverPass = (req, res) => {
  const id = req.userId;
  User.update({
    password: bcrypt.hashSync(req.body.password, 8)
  },{
    where: { id: id }
  })
    .then(num => {
      if (num == 1) {
        res.send({
          message: "Contrase√±a editada!!."
        });
      } else {
        res.send({
          message: `No se pudo editar la contrase√±a!`
        });
      }
    })
    .catch(err => {
      res.status(500).send({
        message: "Error updating User with id=" + id
      });
    });
};